<?phpclass Paths{    static $helper = array('app/system/views/helpers/');    static $view = array('app/system/views/actions/');    static $component = array('app/system/components/');    static $element = array('app/system/views/elements/');    static $controller = array('app/system/controllers/');    static $layout = array('app/system/views/layouts/');    static function set($type, $data, $clear = false)    {        if (!isset(self::$$type)) {            die('Neznamy typ cesty "' . $type . '"');        }        if ($clear == true) {            self::$$type = array();        }        if (is_string($data)) {            array_push(self::$$type, $data);        }        if (is_array($data)) {            array_push(self::$$type, $data);        }    }    static function findPath($type = null, $file = null)    {        if ($type == null or !isset(self::$$type)) {            die('Neznamy typ cesty');        }        if ($file == null)            return false;        $sFileToLoad = "";        foreach (self::$$type as $path) {//            echo self::findRoot() . $path . $file . "</br>\n";            if (file_exists(self::findRoot() . $path . $file)) {                $sFileToLoad = self::findRoot() . $path . $file;                //fix for priority of loading when project is running Pawel 2011-07-18                if (defined('PROJECT_PATH')) {                    if (strpos($path, 'projects')) {                        break;                    }                } else {                    break;                }            }        }        if (!empty($sFileToLoad))            return $sFileToLoad;        return false;    }    static function findRoot()    {        return './';    }    static function getPath($sClassName, $sProjectName)    {        foreach (self::$$sClassName as $row) {            if (strpos($row, $sProjectName)) {                return self::findRoot() . ltrim($row, '/');            }        }    }}?>